function gcd(a, b) {
    if (a == 0 || b == 0) 
        return a + b;
    while(a != b) {
        if (a > b)
            a -= b;
        else b -= a;
    }

    return a;
}

function smallestCommon(a, b) {
    return a * b / gcd(a, b);
}


function smallestCommons(arr) {
    const [a, b] = arr[0] < arr[1] ? [arr[0], arr[1]] : [arr[1], arr[0]];

    let temp = smallestCommon(a, a+1);

    if (Math.abs(a-b) >= 2) {
        for (let i = a + 2 ; i <= b; i++) {
            temp = smallestCommon(temp, i);
        }        
    }

    return temp;

}

